"use strict";
var React = require("react");
var Router = require("react-router-component");
var Locations = Router.Locations;
var Location = Router.Location;

var CollectionList = require("./list");
var CollectionDetail = require("./detail");

module.exports = React.createClass({
  render: function () {
    return (
      Locations({ contextual: true },
        Location({ path: "/", handler: CollectionList, questors: this.props.questors }),
        Location({ path: "/:questor", handler: CollectionDetail, questors: this.props.questors })
      )
    );
  }
});
