"use strict";
var React = require("react");
var Router = require("react-router-component");
var Link = Router.Link;

module.exports = React.createClass({
  render: function () {
    var rows = [];
    this.props.questors.forEach(function (questor) {
      rows.push(
        React.DOM.li(null,
          Link({ href: questor.name }, questor.name)
        )
      );
    });

    return (
      React.DOM.ul(null, rows)
    );
  }
});
